mapper.insert:

INSERT INTO QUIZ( DOC_CODE
                , QUIZ_NUMBER
                , QUIZ_CODE
                , QUIZ_TITLE
                , QUIZ_SUBTITLE
                , PATTERN_CODE
                , QUIZ_LEVEL
                , QUIZ_SOURCE
                , CREATE_DATE
                , MODIFY_DATE
                , QUIZ_MEMO
                , QUIZ_ORG_NUMBER
                , QUIZ_ANSWER
                , QUIZ_DESC)
VALUES ( #{doc_code}
       , #{quiz_number}
       , #{quiz_code}
       , REPLACE(REPLACE(#{quiz_title}, '<p>', ''), '</p>', '')
       , #{quiz_subtitle}
       , #{pattern_code}
       , #{quiz_level}
       , #{quiz_source}
       , now()
       , now()
       , #{quiz_memo}
       , #{quiz_org_number}
       , #{quiz_answer}
       , #{quiz_desc})

mapper.update:

UPDATE
    QUIZ
SET PATTERN_CODE = #{pattern_code},
    QUIZ_LEVEL   = #{quiz_level}
WHERE DOC_CODE = #{doc_code}
  AND QUIZ_NUMBER = #{quiz_number}


UPDATE
    QUIZ
SET QUIZ_DESC = #{quiz_desc}
WHERE DOC_CODE = #{doc_code}
  AND QUIZ_NUMBER = #{quiz_number}
