mapper.select:
        SELECT COUNT(*)
        FROM QUIZ_STAT A
        WHERE QUIZ_DATE = #{quiz_date}
          AND DOC_CODE = #{doc_code}
		      AND QUIZ_NUMBER = #{quiz_number}

mapper.update:

        UPDATE
            QUIZ_STAT
        SET QUIZ_NUM= QUIZ_NUM + 1
        WHERE QUIZ_DATE = #{quiz_date}
          AND DOC_CODE = #{doc_code}
		      AND QUIZ_NUMBER = #{quiz_number}
